(248, 1, '2020-05-03 23:04:41', '2020-05-03 21:04:41', '<!-- wp:acf/main-slider {\n    \"id\": \"block_5e92fbaa0f2f9\",\n    \"name\": \"acf\\/main-slider\",\n    \"data\": {\n        \"main_slider_0_title\": \"OPROGRAMOWANIE GASTRONOMICZ\",\n        \"_main_slider_0_title\": \"field_5e92f50d54258\",\n        \"main_slider_0_background\": 91,\n        \"_main_slider_0_background\": \"field_5e92f51e54259\",\n        \"main_slider_1_title\": \"TERMINALE PŁATNICZE\",\n        \"_main_slider_1_title\": \"field_5e92f50d54258\",\n        \"main_slider_1_background\": 92,\n        \"_main_slider_1_background\": \"field_5e92f51e54259\",\n        \"main_slider_2_title\": \"KASY FISKLANE\",\n        \"_main_slider_2_title\": \"field_5e92f50d54258\",\n        \"main_slider_2_background\": 93,\n        \"_main_slider_2_background\": \"field_5e92f51e54259\",\n        \"main_slider\": 3,\n        \"_main_slider\": \"field_5e92f50154257\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/text-area-1 {\n    \"id\": \"block_5e930d9ac39d3\",\n    \"name\": \"acf\\/text-area-1\",\n    \"data\": {\n        \"heading_1\": \"LOOP\",\n        \"_heading_1\": \"field_5e930cbe99dba\",\n        \"heading_2\": \"Autoryzowany Partner ELZAB\",\n        \"_heading_2\": \"field_5e930cd499dbb\",\n        \"content\": \"Witamy na oficjalnej stronie LOOP KONRAD WARMUS - Partnera Handlowego ELZAB. Obsługujemy Klientów oraz prowadzimy sprzedaż kas fiskalnych, drukarek fiskalnych, urządzeń automatycznej identyfikacji towarów oraz szereg innych urządzeń sygnowanych marką ELZAB. Nasza siedziba znajduje się w mieście Kraków.\",\n        \"_content\": \"field_5e930cdc99dbc\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/about-company {\n    \"id\": \"block_5e9318006de1f\",\n    \"name\": \"acf\\/about-company\",\n    \"data\": {\n        \"about_company_section_0_icon\": 146,\n        \"_about_company_section_0_icon\": \"field_5e9312ef8fd2a\",\n    